        imageArrayList.add("data:image/jpeg;base64, ");
        textArrayList.add("Gyms");
        webArrayList.add("");


        imageArrayList.add("https://tarponbayexplorers.com/wp-content/uploads/2015/10/GiftShop5.jpg");
        textArrayList.add("Gift Shops");
        webArrayList.add("");


        imageArrayList.add("https://img.etimg.com/thumb/height-450,width-800,msid-65540338,imgsize-155795/bank-thinkstock.jpg ");
        textArrayList.add("Banks");
        webArrayList.add("");


        imageArrayList.add("https://d36tnp772eyphs.cloudfront.net/blogs/1/2019/06/Row-of-ATMs-in-Thailand-1200x757.jpg");
